实例











            var score = 85;
            //如果分数>90优秀
            if(score >= 90) {
                console.debug('优秀');
                //如果分数>70良好
            } else if(score >= 70) {
                console.debug('良好');
                //如果分数>60及格
            } else if(score >= 60) {
                console.debug('及格');
                //不及格
            } else {
                console.debug('不及格')
            }

            //状态分支语句
            switch(parseInt(score / 10)) {
                case 10:
                case 9:
                    console.debug('优秀');
                    break;
                case 8:
                case 7:
                    console.debug('良好');
                    break;
                case 6:
                    console.debug('及格');
                    break;
                default:
                    console.debug('不及格');
                    break;
            }





