document.documentElement.onkeydown = function (event) {
    var keycode = event.keyCode;
    var ele = document.getElementById('box');
    var x = ele.offsetLeft;
    var y =ele.offsetTop;
    switch (keycode){
        case 37:
            ele.style.left=x-10+'px';
            break;
        case 38:
            ele.style.top=y-10+'px';
            break;
        case 39:
            ele.style.left=x+10+'px';
            break;
        case 40:
            ele.style.top=y+10+'px';
            break;
    }
}




