    //>>4.设置SQL上的参数
    $stmt->bind_param("s", $city);
    //>>5.执行SQL
    $stmt->execute();
    //>>6.获取执行结果
    $result = $stmt->get_result();
    //>>7.接的结果中的每行数据
    while ($row = $result->fetch_assoc()) {
            var_dump($row);
    }
    //>>8.关闭执行结果
    $stmt->close();
}
    //>>9.关闭连接
